    console.log(name,value)
}

    return (
      <div>
        <form>
          <input name="title" placeholder="Title" />
          <textarea name="content" placeholder="Take a note..." rows="3" />
          <button onClick = {updateNoteDraft}>Add</button>
        </form>
      </div>
    );
  }

  export default Form;
