import React, { useState } from "react"

function Form(props) {
const [noteDraft, setNoteDraft]=useState({title:"",content:""})
const [noteSubmitted, setNoteSubmitted] =useState([])


function submitNotes(event){

setNoteSubmitted(preValue => {return [...preValue,noteDraft]}); // return an array
() => props.notesPosted(noteSubmitted)

 event.preventDefault()
    }

function noteTitleDraft(event){
    const {name,value} = event.target
setNoteDraft(preValue => {return {...preValue,[name]:value}}); // return a controlled value
}

function noteContentDrat(event){
const {name,value} = event.target
setNoteDraft(preValue => {return {...preValue,[name]:value}}); // return a controlled value
}


    return (
      <div>
        <form >
          <input onChange ={noteTitleDraft} value ={noteDraft.title} name="title" placeholder="Title" />
          <textarea onChange ={noteContentDrat} value ={noteDraft.content} name="content" placeholder="Take a note..." rows="3" />
          <button onClick = {submitNotes}>Add</button>
        </form>
      </div>
    );
  }

  export default Form;
