import React, { useState } from "react"

function Form() {
const [noteDraft, setNoteDraft]=useState({title:"",content:""})
const [status,setStatus]=useState(false)

function noteTitleDraft(event){
    const {name,value} = event.target
setNoteDraft(preValue => {return {...preValue,[name]:value}});
}

function noteContentDrat(event){
const {name,value} = event.target
setNoteDraft(preValue => {return status? {...preValue,[name]:value}:null});
}

function statusUpdate(event){
 setStatus(preStatus=> !preStatus)
 event.preventDefault()
}

    return (
      <div>
        <form >
          <input onChange ={noteTitleDraft} name="title" placeholder="Title" />
          <textarea onChange ={noteContentDrat} name="content" placeholder="Take a note..." rows="3" />
          <button onClick = {statusUpdate}>Add</button>
        </form>
      </div>
    );
  }

  export default Form;
